import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AdminDashboard.css';

export default function AdminDashboard() {
  const [formData, setFormData] = useState({
    question: '',
    options: ['', '']
  });
  const [polls, setPolls] = useState([]);
  const [sessionCode, setSessionCode] = useState('');
  const [createdPollId, setCreatedPollId] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/poll/history`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        withCredentials: true
      });
      setPolls(res.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to fetch history');
    }
  };

  const handleOptionChange = (index, value) => {
    const newOptions = [...formData.options];
    newOptions[index] = value;
    setFormData(prev => ({ ...prev, options: newOptions }));
  };

  const addOption = () => {
    setFormData(prev => ({
      ...prev,
      options: [...prev.options, '']
    }));
  };

  const removeOption = (index) => {
    if (formData.options.length > 2) {
      const newOptions = formData.options.filter((_, i) => i !== index);
      setFormData(prev => ({ ...prev, options: newOptions }));
    }
  };

  const validateForm = () => {
    if (!formData.question.trim()) {
      setError('Question is required');
      return false;
    }
    if (formData.options.some(opt => !opt.trim())) {
      setError('All options must be filled');
      return false;
    }
    return true;
  };

  const handleCreatePoll = async () => {
    setError('');
    if (!validateForm()) return;
    
    setIsLoading(true);
    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/poll/create`,
        formData,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
          withCredentials: true
        }
      );
      setCreatedPollId(res.data._id);
      setFormData({ question: '', options: ['', ''] });
      alert('Poll created! Now launch a session.');
      fetchHistory();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create poll');
    } finally {
      setIsLoading(false);
    }
  };

  const handleStartSession = async (pollId) => {
    setError('');
    setIsLoading(true);
    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/session/start`,
        { pollId },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
          withCredentials: true
        }
      );
      setSessionCode(res.data.code);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to start session');
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div className="admin-dashboard">
      <div className="dashboard-header">
        <h2>Admin Dashboard</h2>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </div>

      <div className="create-poll-section">
        <h3>Create Poll</h3>
        <div className="form-group">
          <input
            placeholder="Question"
            value={formData.question}
            onChange={e => setFormData(prev => ({ ...prev, question: e.target.value }))}
            disabled={isLoading}
          />
        </div>
        {formData.options.map((opt, i) => (
          <div key={i} className="option-group">
            <input
              placeholder={`Option ${i + 1}`}
              value={opt}
              onChange={e => handleOptionChange(i, e.target.value)}
              disabled={isLoading}
            />
            {formData.options.length > 2 && (
              <button 
                className="remove-option-btn"
                onClick={() => removeOption(i)}
                disabled={isLoading}
              >
                ×
              </button>
            )}
          </div>
        ))}
        <div className="button-group">
          <button 
            className="add-option-btn"
            onClick={addOption}
            disabled={isLoading}
          >
            Add Option
          </button>
          <button 
            className="create-poll-btn"
            onClick={handleCreatePoll}
            disabled={isLoading}
          >
            {isLoading ? 'Creating...' : 'Create Poll'}
          </button>
        </div>
      </div>

      <div className="poll-history-section">
        <h3>Poll History</h3>
        <ul className="poll-list">
          {polls.map(poll => (
            <li key={poll._id} className="poll-item">
              <div className="poll-info">
                <h4>{poll.question}</h4>
                <p>Options: {poll.options.join(', ')}</p>
              </div>
              <button 
                className="start-session-btn"
                onClick={() => handleStartSession(poll._id)}
                disabled={isLoading}
              >
                Start Session
              </button>
            </li>
          ))}
        </ul>
      </div>

      {sessionCode && (
        <div className="session-code-section">
          <h3>Session Code: {sessionCode}</h3>
          <button 
            className="join-session-btn"
            onClick={() => navigate(`/session/${sessionCode}`)}
          >
            Go to Live Session
          </button>
        </div>
      )}

      {error && <div className="error-message">{error}</div>}
    </div>
  );
}
